var hourlyPay = prompt("Enter an hourly wage ($/hr)");
var weekHours = prompt("How many hours did you work this week?");

// Create taxRate variable
var taxRate = 0.15;

// Convert hourlyPay and weekHours to numbers
hourlyPay = Number(hourlyPay);
weekHours = Number(weekHours);

// Calculate grossPay
var grossPay = hourlyPay * weekHours;

// Create deduction variable
var deduction = grossPay * taxRate;

// Calculate totalPay
var totalPay = grossPay - deduction;

console.log("Your gross pay is " + grossPay.toFixed(2) + "$");
console.log("Your total pay is " + totalPay.toFixed(2) + "$");

