{
  // Calculate the amount of overtime
  var overtime = hoursWorked - 40;

  console.log("The employee has worked " + overtime + " hours of overtime");

  // Calculate the regular pay
  var regularSalary = 40 * hourlySalary;
  // Calculate the overtime pay
  var overtimeSalary = overtime * hourlySalary * 1.5;

  grossSalary = regularSalary + overtimeSalary;

  // Check if the employee worked for more than 44 hours
  if(hoursWorked > 44)
  {
    grossSalary = grossSalary + 150;
  }
}
else
{
  grossSalary = hoursWorked * hourlySalary;
}

console.log("The employee's gross salary is " + grossSalary.toFixed(2) + "$");

