units.solution:
var sampleNumber = prompt("Please enter a sample number: (1 to 10000)");
sampleNumber = Number(sampleNumber);

initializeGrid(sampleNumber);

// Create the currentDay variable here
var currentDay = 0;

while(currentDay < 50)
{
  for(var row = 0; row < getRows(); row++)
  {
    for(var col = 0; col < getColumns(); col++)
    {
      var cellState = getCellState(row, col);
      var neighbors = getNeighbors(row, col);

      if(cellState == "alive" && (neighbors < 2 || neighbors > 3))
      {
        setEmpty(row, col);
      }
      else if(cellState == "empty" && neighbors == 3)
      {
        setAlive(row, col);
      }
    }
  }

  // Increase the value of currentDay by one here
  currentDay++;

  updateGrid();
}

